mapper.select:
SELECT SOL_NO AS solNo, UNIQ AS uniq, ORI_UNIQ AS oriUniq, BUNDLE_NO AS bundleNo, ORD_STATUS AS ordStatus, SKU_CD AS skuCd, CARR_NO AS carrNo, CARR_NAME AS carrName, INVOICE_NO AS invoiceNo FROM UNILITE.IF_ORDER_LIST_PUT WHERE UNIQ IN (
#{uniq}
) AND APPLY_FLAG = 'N' ORDER BY INSERT_DB_TIME DESC

mapper.update:
UPDATE UNILITE.IF_ORDER_LIST_PUT SET APPLY_FLAG = 'Y' WHERE UNIQ IN (
#{uniq}
) AND SOL_NO = '9999'

mapper.select:
SELECT ITEM_LEVEL1 , LEVEL_NAME1 , ITEM_LEVEL2 , LEVEL_NAME2 , ITEM_LEVEL3 , LEVEL_NAME3 , ITEM_CODE , ITEM_NAME , SPEC , ITEM_P
FROM UNILITE.SHOP_PURCH_PRICE_V where 1=1
AND ITEM_LEVEL1 = #{itemLevel1}
AND ITEM_LEVEL2 = #{itemLevel2}
AND ITEM_LEVEL3 = #{itemLevel3}
AND ITEM_NAME LIKE CONCAT('%', #{itemName}, '%')

SELECT DISTINCT ITEM_LEVEL1 , LEVEL_NAME1 , ITEM_LEVEL2 , LEVEL_NAME2 FROM UNILITE.SHOP_PURCH_PRICE_V where 1=1
AND ITEM_LEVEL1 IN (#{itemLevel1}, #{itemLevel1_2})
AND ITEM_LEVEL1 = #{itemLevel1}
AND ITEM_LEVEL2 = #{itemLevel2}
AND ITEM_LEVEL3 = #{itemLevel3}
