mapper.select:
SELECT C1.CATEGORY_GROUP_ID , C1.CATEGORY_ID , C1.CATEGORY_CODE , C1.CATEGORY_NAME , C1.CATEGORY_FLAG , C1.CATEGORY_URL , C1.ORDERING , C1.CATEGORY_CLASS1 , C1.CATEGORY_TYPE ,( SELECT MAX(CATEGORY_LEVEL) FROM OP_CATEGORY WHERE CATEGORY_CLASS1 = C1.CATEGORY_CLASS1 ) AS CHILD_MAX_LEVEL , C2.CATEGORY_COUNT FROM ( SELECT CATEGORY_GROUP_ID, CATEGORY_ID, CATEGORY_CODE , CATEGORY_NAME , CATEGORY_CLASS1, CATEGORY_URL , CATEGORY_FLAG, ORDERING, CATEGORY_TYPE FROM OP_CATEGORY WHERE CATEGORY_LEVEL = '1'
AND CATEGORY_GROUP_ID = #{categoryGroupId}
ORDER BY ORDERING ASC ) AS C1 LEFT JOIN ( SELECT CATEGORY_CLASS1, COUNT(CATEGORY_CLASS1) AS CATEGORY_COUNT FROM OP_CATEGORY GROUP BY CATEGORY_CLASS1 ) AS C2 ON C1.CATEGORY_CLASS1 = C2.CATEGORY_CLASS1 ORDER BY C1.ORDERING ASC

mapper.update:
UPDATE OP_CATEGORY SET 
	CATEGORY_GROUP_ID = #{categoryGroupId}
WHERE CATEGORY_CODE = #{categoryCode}

mapper.select:
SELECT * FROM (

) T
ORDER BY T.ORDERING ASC, T.CATEGORY_LEVEL ASC

SELECT IFNULL(CASE WHEN #{categoryLevel} = '1' THEN CONCAT(CATEGORY_CODE,'000000000') WHEN #{categoryLevel} = '2' THEN CONCAT(CATEGORY_CLASS1, CATEGORY_CODE,'000000') WHEN #{categoryLevel} = '3' THEN CONCAT(CATEGORY_CLASS1, CATEGORY_CLASS2, CATEGORY_CODE,'000') WHEN #{categoryLevel} = '4' THEN CONCAT(CATEGORY_CLASS1, CATEGORY_CLASS2, CATEGORY_CLASS3, CATEGORY_CODE) END, '100000000000') AS CATEGORY_CODE FROM ( SELECT
CASE WHEN length(MAX(CATEGORY_CLASS1)+1) = 1 THEN CONCAT('00',MAX(CATEGORY_CLASS1)+1) WHEN length(MAX(CATEGORY_CLASS1)+1) = 2 THEN CONCAT('0',MAX(CATEGORY_CLASS1)+1) WHEN length(MAX(CATEGORY_CLASS1)+1) = 3 THEN MAX(CATEGORY_CLASS1)+1 END AS CATEGORY_CODE
CASE WHEN length(MAX(CATEGORY_CLASS2)+1) = 1 THEN CONCAT('00',MAX(CATEGORY_CLASS2)+1) WHEN length(MAX(CATEGORY_CLASS2)+1) = 2 THEN CONCAT('0',MAX(CATEGORY_CLASS2)+1) WHEN length(MAX(CATEGORY_CLASS2)+1) = 3 THEN MAX(CATEGORY_CLASS2)+1 END AS CATEGORY_CODE
CASE WHEN length(MAX(CATEGORY_CLASS3)+1) = 1 THEN CONCAT('00',MAX(CATEGORY_CLASS3)+1) WHEN length(MAX(CATEGORY_CLASS3)+1) = 2 THEN CONCAT('0',MAX(CATEGORY_CLASS3)+1) WHEN length(MAX(CATEGORY_CLASS3)+1) = 3 THEN MAX(CATEGORY_CLASS3)+1 END AS CATEGORY_CODE
, CATEGORY_CLASS1 , CATEGORY_CLASS2 , CATEGORY_CLASS3 , CATEGORY_CLASS4 FROM OP_CATEGORY
AND CATEGORY_CLASS1 = #{categoryClass1}
AND CATEGORY_CLASS2 = #{categoryClass2}
AND CATEGORY_CLASS3 = #{categoryClass3}
) AS C

SELECT 
	IFNULL(MAX(ORDERING),0)+1 
FROM 
	OP_CATEGORY
WHERE 
	CATEGORY_LEVEL = #{categoryLevel}

AND 
	CATEGORY_CLASS1 = #{categoryClass1}


AND 
	CATEGORY_CLASS2 = #{categoryClass2}


AND 
	CATEGORY_CLASS3 = #{categoryClass3}

mapper.update:
UPDATE OP_CATEGORY SET
	CATEGORY_GROUP_ID = #{categoryGroupId}
WHERE CATEGORY_CODE LIKE CONCAT(#{categoryCode},'%')

UPDATE OP_CATEGORY SET
	CATEGORY_FLAG = #{categoryFlag} 
WHERE CATEGORY_CODE LIKE CONCAT(#{categoryCode},'%')

UPDATE OP_CATEGORY SET
	ORDERING = #{ordering}
WHERE CATEGORY_ID = #{categoryId}

mapper.select:
SELECT COUNT(*) FROM ( SELECT CATEGORY_LEVEL FROM OP_CATEGORY
AND CATEGORY_CLASS1 = #{categoryClass1}
AND CATEGORY_CLASS2 = #{categoryClass2}
AND CATEGORY_CLASS3 = #{categoryClass3}
AND CATEGORY_LEVEL >= #{categoryLevel}
GROUP BY CATEGORY_LEVEL ) AS A

SELECT 
	* 
FROM
	OP_CATEGORY
WHERE
	CATEGORY_CODE NOT IN(#{categoryCode})
AND
	CATEGORY_LEVEL = #{categoryLevel}

AND 
	CATEGORY_CLASS1 = #{categoryClass1}


AND 
	CATEGORY_CLASS2 = #{categoryClass2}


AND 
	CATEGORY_CLASS3 = #{categoryClass3}

AND 
	ORDERING >= #{ordering}

DELETE FROM
	OP_CATEGORY


	AND 
		CATEGORY_CLASS1 = #{categoryClass1}


	AND 
		CATEGORY_CLASS2 = #{categoryClass2}


	AND 
		CATEGORY_CLASS3 = #{categoryClass3}


	AND 
		CATEGORY_CLASS4 = #{categoryClass4}

mapper.update:
UPDATE OP_CATEGORY SET
	CATEGORY_HEADER = #{categoryHeader} ,
	CATEGORY_FOOTER = #{categoryFooter} ,
	CATEGORY_MOBILE_HTML = #{categoryMobileHtml}


		, CATEGORY_MOBILE_HTML_HEADER = #{categoryMobileHtmlHeader}


WHERE CATEGORY_ID = #{categoryId}

mapper.select:
SELECT COUNT(*) FROM ( SELECT CATEGORY_URL FROM OP_CATEGORY
AND CATEGORY_URL = #{categoryUrl}
AND CATEGORY_CODE = #{categoryCode}
UNION ALL
SELECT CODE FROM OP_CATEGORY_GROUP
WHERE CODE = #{categoryUrl}
UNION ALL
SELECT CODE FROM OP_CATEGORY_TEAM
WHERE CODE = #{categoryUrl}
) AS T

mapper.update:
UPDATE OP_CATEGORY SET
	ORDERING = #{ordering}

WHERE CATEGORY_GROUP_ID = #{categoryGroupId} 
	AND CATEGORY_CLASS1 = #{newCode} AND CATEGORY_LEVEL = '1'

UPDATE OP_CATEGORY SET
	ORDERING = ORDERING + 1


	CATEGORY_LEVEL = #{categoryLevel}


		AND CATEGORY_CODE LIKE CONCAT(#{parentCode}, '%')



 		AND CATEGORY_GROUP_ID = #{categoryGroupId}



	AND ORDERING > #{previousOrdering}

UPDATE OP_CATEGORY
CATEGORY_CODE = #{categoryCode}, CATEGORY_CLASS1 = #{categoryClass1}, CATEGORY_CLASS2 = #{categoryClass2}, CATEGORY_CLASS3 = #{categoryClass3}, CATEGORY_CLASS4 = #{categoryClass4}, CATEGORY_LEVEL = #{categoryLevel}, ORDERING = #{ordering},
CATEGORY_GROUP_ID = #{categoryGroupId}
WHERE CATEGORY_CODE LIKE CONCAT(#{currentCode}, '%') AND CATEGORY_LEVEL = #{currentLevel}
