mapper.sql:
SELECT I.ITEM_ID, I.ITEM_USER_CODE, I.ITEM_NAME, I.NONMEMBER_ORDER_TYPE, I.ITEM_TYPE, I.TAX_TYPE, I.ITEM_PRICE, I.SALE_PRICE, I.SALE_PRICE_NONMEMBER, I.ITEM_SUMMARY, I.ITEM_IMAGE, I.ITEM_OPTION_FLAG, I.SEO_INDEX_FLAG FROM ( SELECT DISTINCT CATEGORY_ID FROM OP_CATEGORY C, (
SELECT SUBSTRING(C.CATEGORY_CODE, 1, C.CATEGORY_LEVEL * 3) CATE_CLASS FROM OP_CATEGORY_TEAM CT INNER JOIN OP_CATEGORY_GROUP CG ON CT.CATEGORY_TEAM_ID = CG.CATEGORY_TEAM_ID INNER JOIN OP_CATEGORY C ON CG.CATEGORY_GROUP_ID = C.CATEGORY_GROUP_ID
C.CATEGORY_URL = #{categoryUrl}
CG.CODE = #{categoryGroupCode}
CT.CODE = #{categoryTeamCode}
) C2 WHERE C.CATEGORY_CODE LIKE CONCAT(C2.CATE_CLASS, '%') AND C.CATEGORY_FLAG = 'Y' ) C INNER JOIN OP_ITEM_CATEGORY IC ON C.CATEGORY_ID = IC.CATEGORY_ID INNER JOIN OP_ITEM I ON IC.ITEM_ID = I.ITEM_ID

SELECT I.* FROM ( SELECT DISTINCT CATEGORY_ID FROM OP_CATEGORY C, (
SELECT SUBSTRING(C.CATEGORY_CODE, 1, C.CATEGORY_LEVEL * 3) CATE_CLASS FROM OP_CATEGORY_TEAM CT INNER JOIN OP_CATEGORY_GROUP CG ON CT.CATEGORY_TEAM_ID = CG.CATEGORY_TEAM_ID INNER JOIN OP_CATEGORY C ON CG.CATEGORY_GROUP_ID = C.CATEGORY_GROUP_ID
C.CATEGORY_URL = #{categoryUrl}
CG.CODE = #{categoryGroupCode}
CT.CODE = #{categoryTeamCode}
) C2 WHERE C.CATEGORY_CODE LIKE CONCAT(C2.CATE_CLASS, '%') AND C.CATEGORY_FLAG = 'Y' ) C INNER JOIN OP_ITEM_CATEGORY IC ON C.CATEGORY_ID = IC.CATEGORY_ID INNER JOIN OP_ITEM I ON IC.ITEM_ID = I.ITEM_ID

