mapper.select:
SELECT I.* FROM OP_ITEM I INNER JOIN ( SELECT T.ITEM_ID FROM ( SELECT I.ITEM_ID, CONCAT(',', GROUP_CONCAT(IO.OPTION_STOCK_CODE), ',') AS OPTION_STOCK_CODES, COUNT(DISTINCT IO.OPTION_NAME1) AS COUNT FROM OP_ITEM I INNER JOIN OP_ITEM_OPTION IO ON I.ITEM_ID = IO.ITEM_ID WHERE I.ITEM_ID IN ( SELECT DISTINCT I.ITEM_ID FROM OP_ITEM_OPTION IO INNER JOIN OP_ITEM I ON IO.ITEM_ID = I.ITEM_ID WHERE IO.OPTION_STOCK_CODE IN
#{optionStockCode}
AND I.ITEM_USER_CODE = #{itemUserCode}
) AND I.DATA_STATUS_CODE = '1' AND IO.OPTION_HIDE_FLAG = 'Y' GROUP BY I.ITEM_ID ) T WHERE
T.OPTION_STOCK_CODES LIKE CONCAT('%,', #{optionStockCode}, ',%')
AND T.COUNT = #{count} ) TT ON I.ITEM_ID = TT.ITEM_ID

SELECT C.CATEGORY_ID, C.CATEGORY_NAME, IA.*, I.ITEM_ID AS ADDITION_ITEM_ID, I.ITEM_USER_CODE AS ADDITION_ITEM_USER_CODE, I.ITEM_NAME AS ADDITION_ITEM_NAME, I.NONMEMBER_ORDER_TYPE AS ADDITION_NONMEMBER_ORDER_TYPE, I.ITEM_TYPE AS ADDITION_ITEM_TYPE, I.TAX_TYPE AS ADDITION_TAX_TYPE, I.ITEM_PRICE AS ADDITION_ITEM_PRICE, I.SALE_PRICE AS ADDITION_SALE_PRICE, I.SALE_PRICE_NONMEMBER AS ADDITION_SALE_PRICE_NONMEMBER, I.ITEM_SUMMARY AS ADDITION_ITEM_SUMMARY, I.ITEM_IMAGE AS ADDITION_ITEM_IMAGE, I.ITEM_OPTION_FLAG AS ADDITION_ITEM_OPTION_FLAG, I.SEO_INDEX_FLAG AS ADDITION_SEO_INDEX_FLAG, I.SELLER_DISCOUNT_FLAG, I.SELLER_DISCOUNT_TYPE, I.SELLER_DISCOUNT_AMOUNT, I.SPOT_FLAG, I.SPOT_TYPE, I.SPOT_DATE_TYPE, I.SPOT_DISCOUNT_AMOUNT, I.SPOT_START_DATE, I.SPOT_END_DATE, I.SPOT_START_TIME, I.SPOT_END_TIME, I.SPOT_WEEK_DAY, I.QUICK_DELIVERY_EXTRA_CHARGE_FLAG FROM OP_ITEM_ADDITION IA INNER JOIN OP_ITEM I ON IA.ADDITION_ITEM_ID = I.ITEM_ID LEFT JOIN OP_ITEM_CATEGORY IC ON IA.ADDITION_ITEM_ID = IC.ITEM_ID LEFT JOIN OP_CATEGORY C ON IC.CATEGORY_ID = C.CATEGORY_ID WHERE IA.ITEM_ID = #{itemId} AND I.DISPLAY_FLAG = 'Y' AND I.ITEM_OPTION_FLAG = 'N' AND C.CATEGORY_FLAG = 'Y'
