SELECT COUNT(*) FROM (SELECT OI.USER_ID, OI.ORDER_CODE, OI.CREATED_DATE, OI.ITEM_ID , OI.ITEM_CODE , OI.ITEM_USER_CODE, OI.ITEM_NAME, OI.IMAGE_SRC AS ITEM_IMAGE
OI.USER_ID = #{userId} AND OI.GUEST_FLAG = 'N' AND OI.ORDER_STATUS = '40'
AND O.CREATED_DATE >= DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 90 DAY),'%Y%m%d%H%i%s')
AND OI.ITEM_USER_CODE = #{itemUserCode}
AND OI.ADDITION_ITEM_FLAG = #{additionItemFlag}
) OT LEFT JOIN OP_ITEM_REVIEW IR ON OT.ORDER_CODE = IR.ORDER_CODE AND OT.USER_ID = IR.USER_ID AND OT.ITEM_ID = IR.ITEM_ID
IR.ITEM_REVIEW_ID IS NULL

SELECT OT.USER_ID, OT.ORDER_CODE, OT.ORDER_SEQUENCE, OT.ITEM_SEQUENCE, OT.CREATED_DATE, OT.ITEM_ID , OT.ITEM_CODE , OT.ITEM_USER_CODE, OT.ITEM_NAME, OT.ITEM_IMAGE AS IMAGE_SRC FROM (SELECT OI.USER_ID, OI.ORDER_CODE, OI.ORDER_SEQUENCE, OI.ITEM_SEQUENCE, OI.CREATED_DATE, OI.ITEM_ID , OI.ITEM_CODE , OI.ITEM_USER_CODE, OI.ITEM_NAME, OI.IMAGE_SRC AS ITEM_IMAGE
OI.USER_ID = #{userId} AND OI.GUEST_FLAG = 'N' AND OI.ORDER_STATUS = '40' AND O.CREATED_DATE >= DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 90 DAY),'%Y%m%d%H%i%s')
AND OI.ITEM_USER_CODE = #{itemUserCode}
AND OI.ADDITION_ITEM_FLAG = #{additionItemFlag}
) OT LEFT JOIN OP_ITEM_REVIEW IR ON OT.ORDER_CODE = IR.ORDER_CODE AND OT.USER_ID = IR.USER_ID AND OT.ITEM_ID = IR.ITEM_ID
IR.ITEM_REVIEW_ID IS NULL
ORDER BY IR.CREATED_DATE, OT.ORDER_CODE DESC

SELECT COUNT(*)
FROM OP_ITEM_REVIEW_IMAGE
WHERE ITEM_REVIEW_ID = #{value}

